<?php  /* * Template Name: Default Blog */?><?php get_header(); ?>  <section class="head-en tppp-flt-dv">    <div class="container">      <h1><?=the_title();?></h1>      <hr class="bdr1"/>    </div>  </section>  <section class="bdy-m-sec">    <div class="container">      <div class="flltrr-msec">        <div class="flt-bxx">          <a href="#" class="flltrbtn"><?=__("Filter","ata")?><i class="fa fa-bars"></i></a>          <ul class="oppflt">            <li class="flric"><a href=""><?=__("Categories","ata")?></a>            <?php $categories = get_categories( array(                'orderby' => 'name',                'parent'  => 0            ) ); if(!empty($categories)){?>                <div class="licheak">              <?php foreach($categories as $cat){ ?>                    <div class="checkbox checkbox-info checkbox-circle">                        <input id="<?=$cat->term_id?>" class="styled" type="checkbox">                        <label for="<?=$cat->term_id?>">                            <?= esc_html($cat->name)?>                        </label>                    </div>                <?php }?>              </div>              <?php } ?>            </li>            <li class="flric"><a href=""><?=__("Tags","ata")?> </a>              <?php $tags_array = get_tags(array(                  'orderby' => 'name',                  'parent'  => 0,                  'hide_empty '=>false              ));  if(!empty($tags_array)){?>                  <div class="licheak">                    <?php foreach($tags_array as $tag){ ?>                      <div class="checkbox checkbox-info checkbox-circle">                          <input id="<?=$tag->term_id?>" class="styled" type="checkbox">                          <label for="<?=$tag->term_id?>">                              <?= esc_html($tag->name)?>                          </label>                      </div>                    <?php }?>                </div>              <?php }?>            </li>          </ul>        </li>       </ul>    </div> </section><?php get_footer(); ?>